mapper.select:

SELECT
guild_code
FROM GUILD
WHERE 
GUILD_NAME = #{guild_name}
AND SERVER = #{server}

mapper.insert:

INSERT INTO SURO
(
NICKNAME,
GUILD_CODE,
SURO
)VALUES(
#{nickname},
#{guild_code},
#{suro}
)

mapper.delete:

DELETE FROM SUP
WHERE
GUILD_CODE = #{guild_code}
AND JOB = #{job}
